<template>
  <div class="pt-5">
    <h1 style="color: rgb(233, 231, 231)">Catalogue</h1>
    <div class="list-game">
      <div v-for="game in games" :key="game._id">
        <Card
          :title="game.title"
          :active="game.active"
          imgUrl="data:image/webp;base64,"
        />
      </div>
    </div>
  </div>
</template>

<script>
import Card from "../components/Card.vue";
import Axios from "axios";

export default {
  components: {
    Card,
  },

  data() {
    return {
      games: null,
      isLoading: false,
    };
  },

  created() {
    // GET request using axios to get all the games
    Axios.get("http://localhost:3000/api/games")
      .then((response) => (this.games = response.data))
      .catch((err) => { console.log(err) });
  },
};
</script>

<style lang="scss">
.list-game {
  display: flex !important;
  margin: 50px;
  justify-content: center;
}
</style>